mapper.select:
select r.*, d.name departmentName, ifnull(k.namecn,k.countryname) countryName, f.allowFlow, f.validityDate, f.note from m_terminal r left join m_department d on r.departmentId=d.id and d.status=0 left join m_country k on k.countrycode=r.homeLocation left join m_flow_balance f on r.tsid=f.tsid where 1=1
and r.tsid = #{tsid,jdbcType=INTEGER}
and r.status = #{status,jdbcType=INTEGER}
and r.activated = #{activated,jdbcType=INTEGER}
order by d.id,status
